import{_ as a,c as e,o as i,a5 as s}from"./chunks/framework.DALfHeHd.js";const b=JSON.parse('{"title":"Mac 系统安装 Git","description":"","frontmatter":{},"headers":[],"relativePath":"工具箱/版本管理工具/git/安装/mac.md","filePath":"工具箱/版本管理工具/git/安装/mac.md","lastUpdated":1717317169000}'),t={name:"工具箱/版本管理工具/git/安装/mac.md"},o=s('<h1 id="mac-系统安装-git" tabindex="-1">Mac 系统安装 Git <a class="header-anchor" href="#mac-系统安装-git" aria-label="Permalink to &quot;Mac 系统安装 Git&quot;">​</a></h1><h2 id="方法一-使用-homebrew-安装" tabindex="-1">方法一：使用 Homebrew 安装 <a class="header-anchor" href="#方法一-使用-homebrew-安装" aria-label="Permalink to &quot;方法一：使用 Homebrew 安装&quot;">​</a></h2><p>如果你已经安装了 Homebrew（一个流行的 Mac 包管理器），你可以通过以下命令来安装 Git：</p><ol><li>打开终端（Terminal）。</li><li>输入以下命令并回车：</li></ol><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">brew</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> git</span></span></code></pre></div><p>Homebrew 会自动下载并安装最新版本的 Git。</p><h2 id="方法二-使用-xcode-command-line-tools-安装" tabindex="-1">方法二：使用 Xcode Command Line Tools 安装 <a class="header-anchor" href="#方法二-使用-xcode-command-line-tools-安装" aria-label="Permalink to &quot;方法二：使用 Xcode Command Line Tools 安装&quot;">​</a></h2><p>如果你已经安装了 Xcode 或者 Xcode Command Line Tools，Git 可能已经预装在你的 Mac 上了。你可以通过以下命令来检查是否已经安装了 Git：</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">git</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --version</span></span></code></pre></div><p>如果 Git 已经安装，这个命令会显示 Git 的版本号。如果没有安装，你可以通过以下命令来安装 Xcode Command Line Tools，它会包含 Git：</p><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">xcode-select</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> --install</span></span></code></pre></div>',11),n=[o];function l(c,d,h,p,r,m){return i(),e("div",null,n)}const g=a(t,[["render",l]]);export{b as __pageData,g as default};
