import{_ as t,c as l,o as i,a5 as e}from"./chunks/framework.-E9IboRH.js";const P=JSON.parse('{"title":"什么是 http，http2、http3？","description":"","frontmatter":{},"headers":[],"relativePath":"汇总面试题/网络/2. HTTP协议/什么是http，http2、http3？.md","filePath":"汇总面试题/网络/2. HTTP协议/什么是http，http2、http3？.md"}'),p={name:"汇总面试题/网络/2. HTTP协议/什么是http，http2、http3？.md"},a=e('<h1 id="什么是-http-http2、http3" tabindex="-1">什么是 http，http2、http3？ <a class="header-anchor" href="#什么是-http-http2、http3" aria-label="Permalink to &quot;什么是 http，http2、http3？&quot;">​</a></h1><h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><ol><li><p><strong>HTTP/1.x</strong>：</p><ul><li>基于文本，每次请求/响应都需要建立新的 TCP 连接。</li><li>串行传输，每个请求都必须等待前一个请求完成后才能发送。</li><li>没有多路复用。</li><li>不支持服务器推送，网页加载速度较慢。</li></ul></li><li><p><strong>HTTP/2</strong>：</p><ul><li>多路复用，允许在单个 TCP 连接上并行发送多个请求和响应，提高了传输效率。</li><li>二进制传输，减少了头部数据大小，降低了传输延迟。</li><li>支持服务器推送，可以在客户端请求之前主动推送相关资源，提高了页面加载速度。</li><li>使用头部压缩技术，减少了数据传输的大小，提高了效率。</li></ul></li><li><p><strong>HTTP/3</strong>：</p><ul><li>基于 UDP 协议，取代了 TCP，实现了传输层的快速可靠性。</li><li>多路复用，类似于 HTTP/2，但在网络丢包时具有更好的表现，提高了网络性能和稳定性。</li><li>使用 0-RTT（Zero Round Trip Time）技术，允许客户端在首次连接时发送加密数据，进一步提高了连接速度。</li></ul></li></ol>',3),h=[a];function o(r,_,n,s,T,c){return i(),l("div",null,h)}const u=t(p,[["render",o]]);export{P as __pageData,u as default};
